import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './Post.css';

const Post = ({ post, onUpdate, onDelete }) => {
  const { user } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [editContent, setEditContent] = useState(post.content);
  const [showComments, setShowComments] = useState(false);
  const [commentText, setCommentText] = useState('');
  const [loading, setLoading] = useState(false);

  const isAuthor = user.id === post.author._id;

  const handleLike = async () => {
    try {
      const response = await axios.post(`/posts/${post._id}/like`);
      onUpdate(response.data);
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      const response = await axios.post(`/posts/${post._id}/comment`, {
        text: commentText
      });
      onUpdate(response.data);
      setCommentText('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleUpdate = async () => {
    if (!editContent.trim()) return;
    
    setLoading(true);
    try {
      const response = await axios.put(`/posts/${post._id}`, {
        content: editContent
      });
      onUpdate(response.data);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating post:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        await axios.delete(`/posts/${post._id}`);
        onDelete(post._id);
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString();
  };

  const isLiked = post.likes.includes(user.id);

  return (
    <div className="post">
      <div className="post-header">
        <div className="post-author">
          <div className="author-avatar">
            {post.author.profilePicture ? (
              <img 
                src={`http://localhost:5000/uploads/${post.author.profilePicture}`} 
                alt="Profile" 
              />
            ) : (
              <div className="avatar-placeholder">
                {post.author.name.charAt(0).toUpperCase()}
              </div>
            )}
          </div>
          <div className="author-info">
            <h4>{post.author.name}</h4>
            <span className="post-time">{formatDate(post.createdAt)}</span>
          </div>
        </div>
        
        {isAuthor && (
          <div className="post-actions">
            <button onClick={() => setIsEditing(!isEditing)}>Edit</button>
            <button onClick={handleDelete} className="delete-btn">Delete</button>
          </div>
        )}
      </div>

      {isEditing ? (
        <div className="edit-form">
          <textarea
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
            rows="3"
          />
          <div className="edit-actions">
            <button onClick={handleUpdate} disabled={loading}>
              {loading ? 'Saving...' : 'Save'}
            </button>
            <button onClick={() => setIsEditing(false)}>Cancel</button>
          </div>
        </div>
      ) : (
        <div className="post-content">
          <p>{post.content}</p>
          {post.image && (
            <div className="post-image">
              <img 
                src={`http://localhost:5000/uploads/${post.image}`} 
                alt="Post" 
              />
            </div>
          )}
        </div>
      )}

      <div className="post-stats">
        <span>{post.likes.length} likes</span>
        <span>{post.comments.length} comments</span>
      </div>

      <div className="post-interactions">
        <button 
          onClick={handleLike}
          className={isLiked ? 'interaction-btn liked' : 'interaction-btn'}
        >
          üëç {isLiked ? 'Liked' : 'Like'}
        </button>
        <button 
          onClick={() => setShowComments(!showComments)}
          className="interaction-btn"
        >
          üí¨ Comment
        </button>
      </div>

      {showComments && (
        <div className="comments-section">
          <form onSubmit={handleComment} className="comment-form">
            <input
              type="text"
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              placeholder="Write a comment..."
            />
            <button type="submit">Post</button>
          </form>
          
          <div className="comments-list">
            {post.comments.map(comment => (
              <div key={comment._id} className="comment">
                <div className="comment-author">
                  {comment.user.profilePicture ? (
                    <img 
                      src={`http://localhost:5000/uploads/${comment.user.profilePicture}`} 
                      alt="Profile" 
                    />
                  ) : (
                    <div className="avatar-placeholder small">
                      {comment.user.name.charAt(0).toUpperCase()}
                    </div>
                  )}
                </div>
                <div className="comment-content">
                  <strong>{comment.user.name}</strong>
                  <p>{comment.text}</p>
                  <span className="comment-time">
                    {formatDate(comment.createdAt)}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Post;
